import React, { useState } from "react";
import { useNavigation } from "@react-navigation/core";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Button,
  Image,
  Linking,
  TouchableOpacity,
  FlatList
} from "react-native";
import { Alert } from "react-native";
import {LinearGradient} from 'expo-linear-gradient';

const buttonHeight=50;
const textPos=buttonHeight/2;
const SPACING = 20;
const AVATAR_SIZE = 70;
const ICON_SIZE = 80;

const DATA=[
  {
    id: '1',
    title: 'Change Profile Photo',
    text: "TODO:, Update User' profile_photo url in Firebase.",
  },
  {
    id: '2',
    title: 'Change App Theme',
    text: "TODO:, Update User' profile_photo url in Firebase.",
  },
  {
    id: '3',
    title: 'Suggestions?',
    text: "TODO:, Update User' profile_photo url in Firebase.",
  },
];
const Settings = () => {
  const navigation = useNavigation();
  const [name, SetName] = useState("");
  const renderItem2 = ({ item }) => {
    return (
    <View style={{flexDirection: 'row',
    padding:SPACING,
    flex:1,
    paddingTop:10,
    borderRadius:100,
    marginBottom:SPACING/2,
    paddingBottom:5,
  
    }}>
       <View>
         <TouchableHighlight  onPress={() => {
            Alert.alert("TODO:", "Update User' profile_photo url in Firebase.");
          }}>
        </TouchableHighlight>
      </View>
      </View>
    );
    
  };

  return (
    <>
    
    <Image 
       style={{width:ICON_SIZE,
        height:ICON_SIZE,
        position: 'absolute',
        resizeMode: 'cover',
                right: 0,
                top: 10,
        borderRadius:ICON_SIZE
        ,marginRight:SPACING/2,
        borderWidth:0,
        borderColor:"rgba(0,0,0,1)",
        backgroundColor:"rgba(255,255,255,1)",
      shadowOffset:{
      height:10,
      shadowColor:'black'
      },
       shadowOpacity:1,
      shadowRadius:20,
        }}
        source={require("./bear.gif")}
        />
      </>
  );
};

const styles = StyleSheet.create({
  button: {
    alignItems: "center",
    justifyContent: "center",
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    marginTop:1,
    marginBottom:1,
    backgroundColor: "rgba(18,33,65,0.8)",
  },
  text: {
    fontSize: 16,
    lineHeight: 21,
    fontWeight: "bold",
    letterSpacing: 0.25,
    color: "white",
  },
  linearGradient: {
    height: AVATAR_SIZE,
    paddingLeft: 15,
    paddingRight: 15,
    borderRadius: 5,
    paddingTop: AVATAR_SIZE/4,
  },
  buttonText: {
    fontSize: AVATAR_SIZE/3,
    fontWeight:'700',
    textAlign: 'center',
    color: ' rgba(128,163,198,0.8)',
    backgroundColor: 'transparent',
  },
});

export default Settings;
