import React, { useState } from "react";
import { useNavigation } from "@react-navigation/core";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Button,
  Linking,
  TouchableHighlight,
  TouchableOpacity
} from "react-native";
import {LinearGradient} from 'expo-linear-gradient';
import { getSchedule } from './RetrieveSchedules';
import { SafeAreaView } from "react-native-safe-area-context";
import { ScrollView } from "react-native-web";

const buttonHeight=50;
const textPos=buttonHeight/2;
const SPACING = 20;
const AVATAR_SIZE = 70;
const ICON_SIZE = 260;

const Schedule = () => {
  const[state,setState]=useState({scheduleList: [{key:0,
  created_at:"",
  hospital:"",
  ID:"",
  specialty_duration:"",
  specialty_id:"",
  student_id:"",
  updated_at:"",
  week_no:"",
  }]}) 


  const onReceive = (scheduleList) => {

    setState(prevState => ({
      scheduleList: prevState.scheduleList = scheduleList,

    }));
  }
  getSchedule(onReceive)
  console.log(state.scheduleList)
  const navigation = useNavigation();
  const [name, SetName] = useState("");

  return (

    <SafeAreaView style={{flex:1}}>
      <View style ={styles.container}>

        <ScrollView style ={styles.container} >
            {
              state.protocolList.map((item,key)=>(

                <View>
                <TouchableOpacity style = {styles.item} onPress={()=>toogle(key)} >

                  <Text style={styles.itemText}>
                    {item.Protocol}
                  </Text>
                  <Text style={styles.itemText}>
                    {selected===key?"-":"+"}
                  </Text>

                </TouchableOpacity>
                <View style={selected===key? styles.content_show:styles.contentHide}>
                    <Text>{item.content}</Text>
                </View>
              </View>

              )

              )
            }
        </ScrollView>
      </View>


    </SafeAreaView>
     );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor:'cream',
  },
  header:{
  flexDirection:'row',
  padding:10,
  },
  item:{
    backgroundColor:'cream',
    padding:20,
  },
  itemText:{
    fontSize: 16,
    fontWeight:'500',
    color:'darkblue'


  },
  contentHide: {
    cursor: 'pointer',
    backgroundColor: 'lightblue',
    overflow: "hidden",
    maxHeight: 0

  },
  content_show:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    marginBottom:10,
    marginTop:10,
    padding:30,
    opacity: 1,
    maxHeight:9999,
    transition:'opacity 1s linear'

  }
});

export default Schedule;
