import React, { useState } from "react";
import { useNavigation } from "@react-navigation/core";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Button,
  Linking,
} from "react-native";

const Schedule = () => {
  const navigation = useNavigation();
  const [name, SetName] = useState("");

  return (
    <>
      <View style={{width:"100%",flex:1,}}>
      <LinearGradient colors={['rgba(30,55,108,1)', ' rgba(30,55,108,0.8)', 'rgba(30,55,108,1)']} >
      <TouchableOpacity
            onPress={()=>{navigation.navigate("Calendar")}}   // when user clicks on login button 
            >
            <Text style={styles.buttonText}>Calendar</Text>

        </TouchableOpacity> 
    </LinearGradient>
      </View>
      <View style={{width:"100%",flex:1,}}>
      <LinearGradient colors={['rgba(30,55,108,1)', ' rgba(30,55,108,0.8)', 'rgba(30,55,108,1)']} >
      <TouchableOpacity
            onPress={()=>{navigation.navigate("Login")}}   // when user clicks on login button 
            >
            <Text style={styles.buttonText}>Logout</Text>

        </TouchableOpacity> 
    </LinearGradient>
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  button: {
    alignItems: "center",
    justifyContent: "center",
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: "rgba(28,56,107,255)",
  },
  text: {
    fontSize: 16,
    lineHeight: 21,
    fontWeight: "bold",
    letterSpacing: 0.25,
    color: "white",
  },
});

export default Schedule;
