import styled from 'styled-components';
import {View,Text,Image} from 'react-native';
import { Constants } from 'expo-constants';

const statusBarHeight = Constants.statusBarHeight;

//colors
export const colors = {
    primary: '#00bcd4',
    secondary: '#ff9800',
    tertiary: '#ff5722',
    darkLight: '#212121',
    brand: '#00bcd4',
    green: '#4caf50',
    red: '#f44336',
    yellow: '#ffeb3b',
    blue: '#2196f3',

};

const {primary, secondary, tertiary, darkLight, brand, green, red, yellow, blue} = colors;

export const StyledContainer = styled.View`
    flex: 1;
    padding: 25px;
    padding-top: ${statusBarHeight + 10}px;
    background-color: ${primary};`

export const InnerContainer = styled.View`
    flex: 1;
    width: 100%;
    align-items: center;
    justify-content: center;
    background-color: ${primary};
`;
export const pageLogo = styled.Image`
    width: 100px;
    height: 100px;
    margin-bottom: 20px;
`;

export const pageTitle = styled.Text`
    font-size: 24px;
    font-weight: bold;
    color: ${darkLight};
    margin-bottom: 20px;
    padding-bottom: 10px;
`
