import React, { Component } from 'react';
import {
  StyleSheet,
  Text,
  View,
  TouchableHighlight,
  Image
} from 'react-native';
import CalendarPicker from 'react-native-calendar-picker';
import {useNavigation} from '@react-navigation/core'
import {Calendar,CalendarList,Agenda} from 'react-native-calendars';

const buttonHeight=50;
const textPos=buttonHeight/2;
const SPACING = 20;
const AVATAR_SIZE = 70;
const ICON_SIZE = 80;

const CalendarView = () => {
  
  
    
  
    return (

      <Calendar
  markingType={'period'}
  markedDates={{
    '2022-05-20': {textColor: 'green', startingDay:true},
    '2022-05-22': {startingDay: true, color: 'green'},
    '2022-05-23': {selected: true, endingDay: true, color: 'green', textColor: 'gray'},
    '2022-05-04': {disabled: true, startingDay: true, color: 'green', endingDay: true}
  }}
/>
      
    );
}

    
  
const styles = StyleSheet.create({
  container: {
    marginTop:20,
    backgroundColor: 'white',
    marginTop: 10,
    borderRadius:20,
    flex:1,
    borderColor:"rgba(36,50,61,1)",
    borderWidth:5
  },
});
export default CalendarView;
