import React, { Component,useState } from 'react';
import {
  StyleSheet,
  Button,
  FlatList,
  SafeAreaView,
  Text,
  View,
} from 'react-native';
import { getFoods, signout } from './FoodsApi'
import { ListItem, Divider } from 'react-native-elements';
import { color } from 'react-native-elements/dist/helpers';




class FoodList extends Component {


  state = {
    foodList: [{id:0,Protocol:"123",content:"456"}
],
    selectedIndex: null
  }

  onFoodsReceived = (foodList) => {
    console.log(foodList)
    this.setState(prevState => ({
      foodList: prevState.foodList = foodList
    }));
  }

  componentDidMount() {
    getFoods(this.onFoodsReceived);
  }

  //[selected,setSelected] =useState(null)


/*  const renderItem = ({ item }) => (
        <Item title={item.content} />
      );
    return this.state.foodList.length > 0 ?

      <SafeAreaView style={styles.container}>
      <FlatList
        data={this.state.foodList}
        renderItem={renderItem}
        keyExtractor={item => item.Protocol}
      /> */

  render() {

   const toogle = i =>{
      if(selectedIndex==i){
        this.state.selectedIndex=null
        return this.state.selectedIndex

      }
      this.state.selectedIndex=i
  }  
    return (
    <View style={styles.container}>  
    <FlatList  
        data={this.state.foodList}  
        renderItem={({item,index}) =>  
        <View>
            <View style={styles.protocolName} onPress={()=> toogle(index)}>
            <Text >{item.Protocol}</Text>
            <Text >{this.state.selectedIndex === index? '-':'+'}</Text>
            </View>
            <View style={styles.contentStyle}>
            <Text style={this.state.selectedIndex === index? styles.content:styles.content_show}>{item.content}</Text>
            </View>
            </View>

                } 
        ItemSeparatorComponent={this.renderSeparator}  
    />  
</View>  )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  listItem: {
    marginTop: 8,
    marginBottom: 8,
    color: 'black'
  },
  textContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  titleStyle: {
    fontSize: 30,
    color: 'black'
  },
  subtitleStyle: {
    fontSize: 18
  },
  emptyTitle: {
    fontSize: 32,
    marginBottom: 16
  },
  emptySubtitle: {
    fontSize: 18,
    fontStyle: 'italic'
  },
  protocolName:{

    justifyContent:'space-between',
    cursor:'pointer',
    flexDirection:'row',
    backgroundColor:'red',
    margin:20



  },
  contentStyle:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    overflow:"hidden",
   //maxHeight:0

  },
  content_show:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    marginBottom:10,
    marginTop:10,
    padding:30,
    opacity: 1,
    maxHeight:9999,
    transition:'opacity 1s linear'

  }
});

export default FoodList;
