import {firebase} from "../firebase"
import React, { Component, useState } from 'react';
import {useNavigation} from '@react-navigation/core'
import Icon from 'react-native-vector-icons/FontAwesome';
import {
  TextInput,
  Button,
  Linking,
} from 'react-native';
import {Alert, LayoutAnimation, StyleSheet, View, Text, ScrollView, UIManager, TouchableOpacity, Platform, Image } from 'react-native';

class Protocols extends Component{
  state = { data: [] };

    getarray(){
    return this.state.data;
  }

  componentDidMount() {
    firebase
    .firestore()
    .collection("emergency_protocols")
    .get()
      .then(querySnapshot => {
        const Matches = [];
        querySnapshot.forEach(function(documentSnapshot) {
          Matches.push({id:n,Protocol: documentSnapshot.id,content:documentSnapshot.data().protocol_1})
  })
  this.setState({ Matches });
})
.catch(function(error) {
  console.log("Error getting documents: ", error);
});
   }


   render() {
    const [clicked,setClick]=useState(null)
    const toggle =i=>{
      if(clicked==i){
        return setClick(null)
      }
      setClick(i)

    }
    return (


      this.state.data.map((item, i) => (


              <View style={styles.item} >

              <View style={styles.protocolName} >
                <TouchableOpacity key={i} onPress={()=>toggle(i)}>


              <Text style={styles.categoryText}> <Text> 
                  {clicked==i ? '-':'+'} </Text>{item.Protocol}</Text>
              </TouchableOpacity>
              </View>

            <View style= {clicked==i ? styles.content_show:styles.content} ><Text>{item.content}</Text> </View> 
              </View>


          ))


   )

   }

}
     const styles = StyleSheet.create({


        container: {
          flex: 1,
          justifyContent: 'center',
          //paddingTop: (Platform.OS === 'ios') ? 20 : 0,
          backgroundColor: 'white',
        },
        iconStyle: {
          width: 30,
          height: 30,
          justifyContent: 'flex-end',
          alignItems: 'center',
          tintColor: '#fff'
        },
        subCategoryText: {
          fontSize: 18,
          color: '#000',
          padding: 10
        },
        categoryText: {
          textAlign: 'left',
          color: 'blue',
          fontSize: 21,
          padding: 10
        },

        wrapper:{
          cursor:'pointer',
          flex: 1,
          justifyContent: 'center',
          height: '80%',
          width: '80%',
          alignItems:'center'
      },
      protocolName:{

        justifyContent:'space-between',
        cursor:'pointer',
        flexDirection:'row',

        backgroundColor:'cream',



      },
      item:{

        marginBottom:10,
        cursor:'pointer',
        backgroundColor:'white',
        marginTop:5,

        padding:10
      },
      content:{
        cursor:'pointer',
        backgroundColor:'lightblue',
        overflow:"hidden",
        maxHeight:0

      },
      content_show:{
        cursor:'pointer',
        backgroundColor:'lightblue',
        marginBottom:10,
        marginTop:10,
        padding:30,
        opacity: 1,
        maxHeight:9999,
        transition:'opacity 1s linear'

      }



      });

      export default Protocols;
