import React, { useState, useEffect } from 'react';
import {
  StyleSheet,
  SafeAreaView,
  Text,
  View,
  TouchableOpacity,
  ScrollView,


} from 'react-native';
import {firebase} from "../firebase"


function getList(protocolsReceived) {

  var protocolList = []
  var snapshot = firebase.firestore()
    .collection('emergency_protocols')
    .get()

  snapshot.forEach((doc) => {
    protocolList.push({isExpanded:false,Protocol: doc.id,content:doc.data().protocol_1});

  });

  protocolsReceived(protocolList);
}


const Protocols =
  () => {

    const [state, setState] = useState({ protocolList: [{ isExpanded: false, Protocol: "123", content: "456" }] })


    const onReceive = (protocolList) => {
      setState(prevState => ({
        protocolList: prevState.protocolList = protocolList,

      }));
    }
    getList(onReceive);
    return (

      <SafeAreaView style={{ flex: 1 }}>
        <View style={styles.container}>

          <ScrollView >
            {
              state.protocolList.map((item, key) => (

                <View>
                  <TouchableOpacity style={styles.item} >
                    <Text style={styles.itemText}>
                      {item.Protocol}
                    </Text>
                  </TouchableOpacity>
                  <View style={styles.content_show}>

                    <Text>{item.content}</Text>

                  </View>
                </View>

              ))
            }
          </ScrollView>
        </View>


      </SafeAreaView>
    )
  }


const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header: {
    flexDirection: 'row',
    padding: 10,
  },
  item: {
    backgroundColor: 'cream',
    padding: 20,
  },
  itemText: {
    fontSize: 16,
    fontWeight: '500',


  },
  content_show: {
    cursor: 'pointer',
    backgroundColor: 'lightblue',
    marginBottom: 10,
    marginTop: 10,
    padding: 30,
    opacity: 1,
    maxHeight: 9999,
    transition: 'opacity 1s linear'

  }
});


export default Protocols;
