import {firebase} from "../firebase"
import React, { useState } from 'react';
import {useNavigation} from '@react-navigation/core'
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Button,
  Linking,
} from 'react-native';


const Test = () => 
{
    return (
      <View style={styles.panelContainer}>
      <TouchableOpacity activeOpacity={0.8} onPress={this.props.onClickFunction} style={styles.categoryView}>
        <Text style={styles.categoryText}>{this.props.item.id} </Text>
      </TouchableOpacity>
      <View style={{ height: this.state.layoutHeight, overflow: 'hidden' }}>
        {
          this.props.item.subCategory.map((item, key) => (
            <TouchableOpacity key={key} style={styles.subCategoryText} onPress={this.showSelectedCategory.bind(this, item.content)}>
              <Text> {item.content} </Text>
              <View style={{ width: '100%', height: 1, backgroundColor: '#000' }} />
            </TouchableOpacity>
          ))
        }
      </View>
    </View>)

};



const data =[
    {
        id:"1",
        content:"hi there"

    },
    {
        id:"2",
        content:"hi there"

    },
    {
        id:"3",
        content:"hi there"

    },
    {
        id:"4",
        content:"hi there"

    }
]

     const styles = StyleSheet.create({

       wrapper :{
        marginVertical: 5,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: 'white'
       } ,
        container: {
          flex: 1,
          justifyContent: 'center',
          //paddingTop: (Platform.OS === 'ios') ? 20 : 0,
          backgroundColor: 'white',
        },
        iconStyle: {
          width: 30,
          height: 30,
          justifyContent: 'flex-end',
          alignItems: 'center',
          tintColor: '#fff'
        },
        subCategoryText: {
          fontSize: 18,
          color: '#000',
          padding: 10
        },
        categoryText: {
          textAlign: 'left',
          color: '#191970',
          fontSize: 21,
          padding: 10
        },
        categoryView: {
          marginVertical: 5,
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center',
          backgroundColor: 'white'
        },
        Btn: {
          padding: 10,
          backgroundColor: '#FF6F00'
        }
      });

      export default Test;
