import React, {  Component,useState ,useEffect} from 'react';
import {
  StyleSheet,
  Button,
  FlatList,
  SafeAreaView,
  Text,
  View,
  TouchableOpacity,
  ScrollView,
  LayoutAnimation,

} from 'react-native';

import { ListItem, Divider } from 'react-native-elements';
import { color } from 'react-native-elements/dist/helpers';
import {firebase} from "../firebase"


 function getFoods() {

  var foodList = []
  var n = foodList.length

  firebase.firestore()
    .collection('emergency_protocols')
    .get()
    .then(querySnapshot => {
      querySnapshot.forEach(function(doc) {
        console.log(doc.id)
        foodList.push({isExpanded:false,Protocol: doc.id,content:doc.data().protocol_1});
      })
    })


  return foodList
}


const FoodList = ()=>{

  const[state,setState]=useState({foodList: [{isExpanded:false,Protocol:"123",content:"456"}]})
  const[selected,setSelected] =useState(null)

  const onFoodsReceived = (foodList) => {
    console.log(foodList)
    setState(prevState => ({
      foodList: prevState.foodList = foodList, 
    }));
  }
  onFoodsReceived(getFoods())

    const toogle =i=>{

      if(selected==i){
        setSelected(null)
     }
     setSelected(i)

    }

    return (

      <SafeAreaView style={{flex:1}}>
        <View style ={styles.container}>

          <ScrollView >
              {
                state.foodList.map((item,key)=>(

                  <View>
                  <TouchableOpacity style = {styles.protocolName}>
                    <Text style={styles.itemText}>
                      <Text>{selected===key?"-":"+"}</Text>{item.Protocol} 
                    </Text>
                  </TouchableOpacity>
                  <View style={selected===key?styles.content_show:styles.contentStyle}>

                      <Text>{item.content}</Text>

                  </View>
                </View>

                )

                )
              }
          </ScrollView>
        </View>


      </SafeAreaView>  
       )
  }


const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header:{
  flexDirection:'row',
  padding:10,
  },
  item:{
    backgroundColor:'cream',
    padding:20,
  },
  itemText:{
    fontSize: 16,
    fontWeight:'500',


  },
  contentStyle:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    overflow:"hidden",
   maxHeight:0

  },
  content_show:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    marginBottom:10,
    marginTop:10,
    padding:30,
    opacity: 1,

    transition:'opacity 1s linear'

  },
  protocolName:{

    justifyContent:'space-between',
    cursor:'pointer',
    flexDirection:'row',
    backgroundColor:'cream',



  },
});


export default FoodList;
