import React, { Component } from 'react';
import {requireNativeComponent, StyleSheet, Button, View, SafeAreaView, Text, Alert } from 'react-native';
import { useNavigation } from "@react-navigation/core";
import MapView from 'react-native-maps';
const Separator = () => (
  <View style={styles.separator} />
);
const navigation = useNavigation();
class PanicButton  extends Component{
    render(){
return(
  <View style={styles.container}>
    <View>

      <Button
        title="Protocols"
        onPress={() =>  navigation.navigate("Protocols")}

      />
    </View>
    <Separator />
    <View>
      <Button
        title="Press me"
        color="#f194ff"
        onPress={() => Alert.alert('Button with adjusted color pressed')}
      />
    </View>
    <Separator />

    <View>

      <View style={styles.fixToText}>
        <Button
          title="Left button"
          onPress={() => Alert.alert('Left button pressed')}
        />
        <Button
          title="Right button"
          onPress={() => Alert.alert('Right button pressed')}
        />
      </View>
    </View>
    <Separator/>
    <View style={styles.container}>

                <MapView style={styles.map} 
                initialRegion={
                    {latitude:-26.1851374 ,longitude:28.0388106,latitudeDelta:0.1,longitudeDelta:0.1,
                    }
                }></MapView>
            </View>
  </View>
  )
              }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginHorizontal: 16,
  },
  title: {
    textAlign: 'center',
    marginVertical: 8,
  },
  fixToText: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
  map:{
    position:"absolute",
    top:0,
    left:0,
    bottom:0,
    right:0,
}

});

export default PanicButton
