import { getThemeProps } from '@material-ui/styles';
import React, { Component } from 'react';
import { Alert, LayoutAnimation, StyleSheet, View, Text, ScrollView, UIManager, TouchableOpacity, Platform, Image } from 'react-native';
import {firebase} from "../firebase"



class ExpandableListView extends Component {

  constructor() {
    super();

    this.state = {
      layoutHeight: 0
    }
  }

  UNSAFE_componentWillReceiveProps(nextProps) {
    if (nextProps.item.expanded) {
      this.setState(() => {
        return {
          layoutHeight: null
        }
      });
    }
    else {
      this.setState(() => {
        return {
          layoutHeight: 0
        }
      });
    }
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (this.state.layoutHeight !== nextState.layoutHeight) {
      return true;
    }
    return false;
  }

  showSelectedCategory = (item) => {
    Alert.alert(item);
  }

  render() {
    return (
      <View style={styles.panelContainer}>
        <TouchableOpacity activeOpacity={0.8} onPress={this.props.onClickFunction} style={styles.categoryView}>
          <Text style={styles.categoryText}>{this.props.item.category} </Text>
        </TouchableOpacity>
        <View style={{ height: this.state.layoutHeight, overflow: 'hidden' }}>
          {
            this.props.item.subCategory.map((item, key) => (
              <TouchableOpacity key={key} style={styles.subCategoryText} onPress={this.showSelectedCategory.bind(this, item.name)}>
                <Text> {item.name} </Text>
                <View style={{ width: '100%', height: 1, backgroundColor: '#000' }} />
              </TouchableOpacity>
            ))
          }
        </View>
      </View>
    );
  }
}








export default class Protocols extends Component {

  constructor() {
    function getData (){


      var idnum1 =1;
      var idnum2=idnum1+1
      var idnum3=idnum2+1

      var id1 = []

     firebase.firestore()
     .collection('emergency_protocols')
     .get()
     .then(querySnapshot => {

       var size=1;

       querySnapshot.forEach(documentSnapshot => {
         //console.log('User ID: ', documentSnapshot.id+"", documentSnapshot.data());

         id1.push(documentSnapshot.id)

         size++


       });

       for (let i = 0; i < id1.length; i++) {
         console.log(id1[i]);}

     });

     var categoryArray1=[
       {  expanded: false, category: "1234", subCategory: [{ id: idnum1, name: 'Microwave Ovens' },
       { id: idnum2, name: 'Oven Toaster Grills (OTG)' }, { id: idnum3, name: 'Juicer/Mixer/Grinder' }]}
     ];
     for (let i = 0; i < id1.length; i++) {
       const element = id1[i];
       idnum1=idnum3+1
       idnum2=idnum1+1
       idnum3=idnum2+1

       categoryArray1.push({
         expanded: false, category: id1[i], subCategory: [{ id: idnum1, name: 'Microwave Ovens' },
         { id: idnum2, name: 'Oven Toaster Grills (OTG)' }, { id: idnum3, name: 'Juicer/Mixer/Grinder' }]})


     }
     for (let i = 0; i < categoryArray1.length; i++) {
       console.log(categoryArray1[i]);}


       return id1;

     }

    super();
    if (Platform.OS === 'android') {
      UIManager.setLayoutAnimationEnabledExperimental(true)
    }

    // create array to contain Expandable ListView items & create a State named as accordionData and store the array in this State

    const array =[]
    const arr = getData()
    for (let i = 0; i < arr.length; i++) {
      array.push({
        expanded: false, category: arr[i], subCategory: [{ id: idnum1, name: 'Microwave Ovens' },
        { id: idnum2, name: 'Oven Toaster Grills (OTG)' }, { id: idnum3, name: 'Juicer/Mixer/Grinder' }]})

    }

    this.state = { accordionData: [...array] }
  }


  // enable layout animation, toggle 'expanded' state for index and then update the layout
  updateLayout = (index) => {
    LayoutAnimation.configureNext(LayoutAnimation.Presets.easeInEaseOut);
    const array = [...this.state.accordionData];
    array[index]['expanded'] = !array[index]['expanded'];
    this.setState(() => {
      return {
        accordionData: array
      }
    });
  }

  render() {
    return (

      <View style={styles.container}>

        <ScrollView contentContainerStyle={{ paddingHorizontal: 10, paddingVertical: 5 }}>

          {
            this.state.accordionData.map((item, key) =>
              (
                <ExpandableListView key={item.category} onClickFunction={this.updateLayout.bind(this, key)} item={item} />
              ))
          }
        </ScrollView>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingTop: (Platform.OS === 'ios') ? 20 : 0,
    backgroundColor: '#F5FCFF',
  },
  iconStyle: {
    width: 30,
    height: 30,
    justifyContent: 'flex-end',
    alignItems: 'center',
    tintColor: '#fff'
  },
  subCategoryText: {
    fontSize: 18,
    color: '#000',
    padding: 10
  },
  categoryText: {
    textAlign: 'left',
    color: '#191970',
    fontSize: 21,
    padding: 10
  },
  categoryView: {
    marginVertical: 5,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: 'white'
  },
  Btn: {
    padding: 10,
    backgroundColor: '#FF6F00'
  }
});
