import {firebase} from "../firebase"
import React, { useState } from 'react';
import {useNavigation} from '@react-navigation/core'
import Icon from 'react-native-vector-icons/FontAwesome';
import {
  TextInput,
  Button,
  Linking,
} from 'react-native';
import { Alert, LayoutAnimation, StyleSheet, View, Text, ScrollView, UIManager, TouchableOpacity, Platform, Image } from 'react-native';
//import { initializeApp } from "https://www.gstatic.com/firebasejs/9.6.11/firebase-app.js";
import {getFirestore, collection, doc, getDocs} from "firebase/firestore";
import { Firestore, getDoc } from "firebase/firestore";



var data = [
    {
        id:1,
        Protocol:"Asthma_Attack",
        content:" 1) Sit the person upright.  2) Give 4 separate puffs of blue/grey reliever puffer.  3) Wait 4 minutes.  4) If breathing does not return to normal, call for an ambulance."

    }
]


  // Initialize Firebase and variables
 // const app = firebase.initializeApp(firebaseConfig);
 // const analytics = firebase.getAnalytics(app);
 const db = getFirestore();


function getData (){
    const querySnapshot = getDocs(collection(db, "emergency_protocols"));
    querySnapshot.forEach((doc) => {

        /* firebase.firestore()
 .collection('emergency_protocols')
 .get() */
        var protocol_name = doc.data().protocol_name;
        var protocol_description = doc.data().protocol_1;

        AddProtocolToTable(protocol_name, protocol_description);
    });
 }
 function AddProtocolToTable( protocol_name, protocol_description){

    data.push({id:n,Protocol: protocol_name,content:protocol_description} )
}

function test (){
    for (let index = 0; index < data.length; index++) {
        console.log(data[index]);

    }
}




const Test = () => 
{
  const [clicked,setClick]=useState(data)
  const toggle =i=>{
    if(clicked==i){
      return setClick(null)
    }
    setClick(i)

  }
  getData();
  test();
    return (


          data.map((item, i) => (


              <View style={styles.item} >

              <View style={styles.protocolName} >
                <TouchableOpacity key={i} onPress={()=>toggle(i)}>


              <Text style={styles.categoryText}> <Text> 
                  {clicked==i ? '-':'+'} </Text>{item.Protocol}</Text>
              </TouchableOpacity>
              </View>

            <View style= {clicked==i ? styles.content_show:styles.content} ><Text>{item.content}</Text> </View> 
              </View>


          ))


   )


};


     const styles = StyleSheet.create({


        container: {
          flex: 1,
          justifyContent: 'center',
          //paddingTop: (Platform.OS === 'ios') ? 20 : 0,
          backgroundColor: 'white',
        },
        iconStyle: {
          width: 30,
          height: 30,
          justifyContent: 'flex-end',
          alignItems: 'center',
          tintColor: '#fff'
        },
        subCategoryText: {
          fontSize: 18,
          color: '#000',
          padding: 10
        },
        categoryText: {
          textAlign: 'left',
          color: 'blue',
          fontSize: 21,
          padding: 10
        },

        wrapper:{
          cursor:'pointer',
          flex: 1,
          justifyContent: 'center',
          height: '80%',
          width: '80%',
          alignItems:'center'
      },
      protocolName:{

        justifyContent:'space-between',
        cursor:'pointer',
        flexDirection:'row',

        backgroundColor:'cream',



      },
      item:{

        marginBottom:10,
        cursor:'pointer',
        backgroundColor:'white',
        marginTop:5,

        padding:10
      },
      content:{
        cursor:'pointer',
        backgroundColor:'lightblue',
        overflow:"hidden",
        maxHeight:0

      },
      content_show:{
        cursor:'pointer',
        backgroundColor:'lightblue',
        marginBottom:10,
        marginTop:10,
        padding:30,
        opacity: 1,
        maxHeight:9999,
        transition:'opacity 1s linear'

      }



      });

      export default Test;
