import React, { Component,useState } from 'react';
import {
  StyleSheet,
  Button,
  FlatList,
  SafeAreaView,
  Text,
  View,
} from 'react-native';
import { getFoods, signout } from './FoodsApi'
import { ListItem, Divider } from 'react-native-elements';
import { color } from 'react-native-elements/dist/helpers';


class FoodList extends Component {


  state = {
    foodList: [{id:0,Protocol:"123",content:"456"}
],
    selectedIndex: 0
  }

  onFoodsReceived = (foodList) => {
    console.log(foodList)
    this.setState(prevState => ({
      foodList: prevState.foodList = foodList
    }));
  }

  componentDidMount() {
    getFoods(this.onFoodsReceived);
  }

/*  const renderItem = ({ item }) => (
        <Item title={item.content} />
      );
    return this.state.foodList.length > 0 ?

      <SafeAreaView style={styles.container}>
      <FlatList
        data={this.state.foodList}
        renderItem={renderItem}
        keyExtractor={item => item.Protocol}
      /> */

  render() {
      const [selected,setSelected] =useState(null)
      const toogle = i =>{
          if(selected==i){
              return setSelected(null)

          }
          setSelected(i)
      }
    return (
        this.state.foodList.map((item, index) => (
    <View style={styles.container}>  


            <View onPress={()=> toogle(index)}>
            <Text style={styles.protocolName}>{item.Protocol}</Text>

            </View>
            <View style={styles.contentStyle}>
            <Text style={styles.item}>{item.content}</Text>
            </View>



</View>       ))


        )

        }

     }

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  listItem: {
    marginTop: 8,
    marginBottom: 8,
    color: 'black'
  },
  textContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  titleStyle: {
    fontSize: 30,
    color: 'black'
  },
  subtitleStyle: {
    fontSize: 18
  },
  emptyTitle: {
    fontSize: 32,
    marginBottom: 16
  },
  emptySubtitle: {
    fontSize: 18,
    fontStyle: 'italic'
  },
  protocolName:{

    justifyContent:'space-between',
    cursor:'pointer',
    flexDirection:'row',
    backgroundColor:'red',



  },
  contentStyle:{
    cursor:'pointer',
    backgroundColor:'lightblue',

   // maxHeight:0

  },
  content_show:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    marginBottom:10,
    marginTop:10,
    padding:30,
    opacity: 1,
    maxHeight:9999,
    transition:'opacity 1s linear'

  }
});

export default FoodList;
