import React, {  useState } from 'react';
import {
  StyleSheet,
  SafeAreaView,
  Text,
  View,
  TouchableOpacity,
  ScrollView,
} from 'react-native';
import {getList} from './ListApi'

const Protocols =
()=>{

  const[state,setState]=useState({protocolList: [{key:0,Protocol:"123",content:"456"}]})


  const onReceive = (protocolList) => {

    setState(prevState => ({
      protocolList: prevState.protocolList = protocolList,

    }));
  }
  getList(onReceive)


  const[selected,setSelected]=useState(null)

  const toogle=i=>{
    if(selected==i){
      return setSelected(null)
    }
    setSelected(i)
  }

    return (

      <SafeAreaView style={{flex:1}}>
        <View style ={styles.container}>

          <ScrollView >
              {
                state.protocolList.map((item,key)=>(

                  <View>
                  <TouchableOpacity style = {styles.item} onPress={()=>toogle(key)} >

                    <Text style={styles.itemText}>
                      {item.Protocol}
                    </Text>
                    <Text style={styles.itemText}>
                      {selected===key?"-":"+"}
                    </Text>
                  </TouchableOpacity>
                  <View style={selected===key? styles.content_show:styles.contentHide}>
                      <Text>{item.content}</Text>
                  </View>
                </View>

                )

                )
              }
          </ScrollView>
        </View>


      </SafeAreaView>  
       )
  }



const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header:{
  flexDirection:'row',
  padding:10,
  },
  item:{
    backgroundColor:'cream',
    padding:20,
  },
  itemText:{
    fontSize: 16,
    fontWeight:'500',


  },
  contentHide: {
    cursor: 'pointer',
    backgroundColor: 'lightblue',
    overflow: "hidden",
    maxHeight: 0

  },
  content_show:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    marginBottom:10,
    marginTop:10,
    padding:30,
    opacity: 1,
    maxHeight:9999,
    transition:'opacity 1s linear'

  }
});
export default Protocols;
