import React, {  Component,useState ,useEffect} from 'react';
import {
  StyleSheet,
  Button,
  FlatList,
  SafeAreaView,
  Text,
  View,
  TouchableOpacity,
  ScrollView,
  LayoutAnimation,

} from 'react-native';
import { getFoods, signout } from './FoodsApi'
import { ListItem, Divider } from 'react-native-elements';
import { color } from 'react-native-elements/dist/helpers';


const  ExpandableComponent=({item,onClickFunction})=>{
  const [layoutHeight,setlayoutHeight]=useState(0);

  useEffect(()=>{
    if(item.isExpanded){
      setlayoutHeight(null)
    }else{
      setlayoutHeight(0)
    }

  },[item.isExpanded])

  return (
    <View>
      <TouchableOpacity style = {styles.item} onPress={onClickFunction}>
        <Text style={styles.itemText}>
          {item.Protocol}
        </Text>
      </TouchableOpacity>
      <View style={{height:layoutHeight,overflow:'hidden',color:'blue'}}>
        {
          item.content
        }

      </View>
    </View>
  )
}

class FoodList extends Component {


  state = {
    foodList: [{isExpanded:false,Protocol:"123",content:"456"}
],
    selectedIndex: 0
  }

  onFoodsReceived = (foodList) => {
    console.log(foodList)
    this.setState(prevState => ({
      foodList: prevState.foodList = foodList,

    }));
  }

  componentDidMount() {
    getFoods(this.onFoodsReceived);
  }

  //[selected,setSelected] =useState(null)


/*  const renderItem = ({ item }) => (
        <Item title={item.content} />
      );
    return this.state.foodList.length > 0 ?

      <SafeAreaView style={styles.container}>
      <FlatList
        data={this.state.foodList}
        renderItem={renderItem}
        keyExtractor={item => item.Protocol}
      /> */

  render() {

    var data =[{isExpanded:false,Protocol:"123",content:"456"}]

    const [listData,setData]=useState(this.state.foodList)
    const updateLayout=(index)=>{
      LayoutAnimation.configureNext(LayoutAnimation.Presets.easeInEaseOut)
      const arr =[...listData]
      arr.map((val,i)=>
      i===index?(arr[i]['isExpanded'])=!(arr[i]['isExpanded']):(arr[i]['isExpanded'])=false

      )
      setData(arr)
    }
    return (

      <SafeAreaView style={{flex:1}}>
        <View style ={styles.container}>
          <View style ={styles.header}>
              <Text >
                Emergency Protocols 
              </Text>

          </View>
          <ScrollView >
              {
                data.map((item,key)=>(
                <ExpandableComponent
                key={item.Protocol}
                item={item}

                onClickFunction={()=>{
                  updateLayout(key)
                }}

                />)

                )
              }
          </ScrollView>
        </View>


      </SafeAreaView>





       )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header:{
  flexDirection:'row',
  padding:10,
  },
  item:{
    backgroundColor:'cream',
    padding:20,
  },
  itemText:{
    fontSize: 16,
    fontWeight:'500',


  },
});


export default FoodList;
