import React, {  Component,useState ,useEffect} from 'react';
import {
  StyleSheet,
  Button,
  FlatList,
  SafeAreaView,
  Text,
  View,
  TouchableOpacity,
  ScrollView,
  LayoutAnimation,

} from 'react-native';
import { getFoods, signout } from './FoodsApi'
import { ListItem, Divider } from 'react-native-elements';
import { color } from 'react-native-elements/dist/helpers';




const FoodList = ()=>{

  const[state,setState]=useState({foodList: [{isExpanded:false,Protocol:"123",content:"456"}]})


  const onFoodsReceived = (foodList) => {

    setState(prevState => ({
      foodList: prevState.foodList = foodList,

    }));
  }
    getFoods(onFoodsReceived);


    const toogle =i=>{
      console.log(state.foodList)
        state.foodList[i]['isExpanded']==!state.foodList[i]['isExpanded']
        console.log(state.foodList[i]['isExpanded'])

    }
    return (

      <SafeAreaView style={{flex:1}}>
        <View style ={styles.container}>

          <ScrollView >
              {
                state.foodList.map((item,key)=>(

                  <View>
                  <TouchableOpacity style = {styles.protocolName} onPress={toogle(key)}>
                    <Text style={styles.itemText}>
                      <Text>{state.foodList[key]['isExpanded']===true?"-":"+"}</Text>{item.Protocol} 
                    </Text>
                  </TouchableOpacity>
                  <View style={state.foodList[key]['isExpanded']===true?styles.content_show:styles.contentStyle}>

                      <Text>{item.content}</Text>

                  </View>
                </View>

                )

                )
              }
          </ScrollView>
        </View>


      </SafeAreaView>  
       )
  }


const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header:{
  flexDirection:'row',
  padding:10,
  },
  item:{
    backgroundColor:'cream',
    padding:20,
  },
  itemText:{
    fontSize: 16,
    fontWeight:'500',


  },
  contentStyle:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    overflow:"hidden",
   maxHeight:0

  },
  content_show:{
    cursor:'pointer',
    backgroundColor:'lightblue',
    marginBottom:10,
    marginTop:10,
    padding:30,
    opacity: 1,

    transition:'opacity 1s linear'

  },
  protocolName:{

    justifyContent:'space-between',
    cursor:'pointer',
    flexDirection:'row',
    backgroundColor:'cream',



  },
});


export default FoodList;
